import React, { useState, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import images1 from "./images/social.JPG";
// import CheckButton from "react-validation/build/button";
import userservice from "./services/userservice";

import AuthService from "./services/auth.service";
const Login = () => {
  let navigate = useNavigate();
  const form = useRef();
  const checkBtn = useRef();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");
  const onChangeUsername = (e) => {
    const username = e.target.value;
    setUsername(username);
  };
  const onChangePassword = (e) => {
    const password = e.target.value;
    setPassword(password);
  };
  const handleLogin = (e) => {
    e.preventDefault();
    setMessage("");
    setLoading(true);
    // form.current.validateAll();
    
    const user = { username, password};
    // if (checkBtn.current.context._errors.length === 0) {
    //   AuthService.login(username, password).then(
    //     () => {
    //       // navigate("/profile");
    //       window.location.reload();
    //     },
    //     (error) => {
    //       const resMessage =
    //         (error.response &&
    //           error.response.data &&
    //           error.response.data.message) ||
    //         error.message ||
    //         error.toString();
    //       setLoading(false);
    //       setMessage(resMessage);
    //     }
    //   );
    // } else {
    //   setLoading(false);
    // }
    userservice
      .authenticate(user)
      .then((response) => {
        if (response.data.accessToken) {
          localStorage.setItem("user", JSON.stringify(response.data));
        }
        return response.data;
      })
      .catch((error) => {
        console.log("Something went wrong", error);
      });
  };
  return (
    <div id="loginform">
      <Form id="form" onSubmit={handleLogin} ref={form}>
        <h3
          style={{
            textAlign: "center",
            textShadow: "2px 2px black",
            color: "white",
          }}
        >
          LOG IN
        </h3>
        <Form.Group className="mb-3" controlId="formBasicUsername">
          <Form.Label
            style={{ color: "white", fontSize: "45px" }}
            value={username}
            onChange={onChangeUsername}
            // validations={[required]}
          >
            USERNAME
          </Form.Label>
          <Form.Control type="text" placeholder="Enter username" required />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label
            style={{ color: "white", fontSize: "45px" }}
            value={password}
            onChange={onChangePassword}
          >
            Password
          </Form.Label>
          <Form.Control type="password" placeholder="Password" />
        </Form.Group>

        {/* <Button variant=" #2c7f86;" type="submit" id="submit" ref={checkBtn} >
          {loading && (
            <span className="spinner-border spinner-border-sm"></span>
          )}
          Submit
        </Button> */}
        <Link className="btn btn-warning" to={`/profile`}>
          Submit{" "}
        </Link>
        {message && (
          <div className="form-group">
            <div className="alert alert-danger" role="alert">
              {message}
            </div>
          </div>
        )}
        {/* <CheckButton style={{ display: "none" }} ref={checkBtn} /> */}
      </Form>
      <div>
        <footer id="footer1">
          <ul class="navbar-nav">
            <li>
              Get connected with us on
              <img
                src={images1}
                alt="My Image"
                style={{ height: "3rem", width: "27rem" }}
              />
            </li>
          </ul>
        </footer>
      </div>
    </div>
  );
};

export default Login;
