import httpClient from "../http-common2";
let token = localStorage.getItem("token");

const auth = (data) => {
  const p = httpClient.post("/authenticate", data);
  return p;
};
const getUser = (id) => {
  return httpClient.get(`/user/getbyusername/${id}`, {
    headers: {
      Authorization: `Bearer ${token}`,
    },
  });
};

export default { auth, getUser };
