import React from "react";
import { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import userservice from "../services/userservice";
import  images1  from "../images/social.JPG";
const Registration = () => {
  const [username, setName] = useState("");
  const [emailId, setEmail] = useState("");

  const [password, setPass] = useState("");
  const [age, setAge] = useState("");

  const [gender, setGender] = useState("");
  const [ph_no, setPhone] = useState("");
  const [roles, setRoles] = useState("");
  const navigate = useNavigate();
  const { id } = useParams();

  const saveUser = (e) => {
    e.preventDefault();

    const user = { username, emailId, password, age, gender, ph_no, roles, id };

    if (id) {
      //update
      userservice
        .update(user)
        .then((response) => {
          console.log("user data updated successfully", response.data);
          navigate("/popup");
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    } else {
      // create
      userservice
        .create(user)
        .then((response) => {
          console.log("user added successfully", response.data);
          navigate("/popup");
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }
  };

  useEffect(() => {
    if (id) {
      userservice
        .get(id)
        .then((user) => {
          setName(user.data.username);
          setEmail(user.data.emailId);
          setPass(user.data.password);
          setAge(user.data.age);
          setGender(user.data.gender);
          setPhone(user.data.ph_no);
          setRoles(user.data.roles);
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }
  }, []);

  function SubmitBtn() {
    if (username && emailId && password && age && gender && ph_no && roles)
      return (
        <Button
          onClick={(o) => saveUser(o)}
          variant="success"
          type="submit"
          id="submit"
        >
          <Link to="/popup"> Submit</Link>
        </Button>
      );
    else
      return (
        <Button
          onClick={(o) => saveUser(o)}
          variant="warning"
          type="submit"
          id="submit"
          disabled
        >
          Submit
        </Button>
      );
  }

  return (
    <div>
      <Form id="registrationform">
        <h3
          style={{
            textAlign: "center",
            textShadow: "2px 2px black",
            color: "white",
          }}
        >
          REGISTRATION
        </h3>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label style={{ color: "white" }}>UserName</Form.Label>
          <Form.Control
            type="text"
            placeholder="UserName"
            value={username}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label style={{ color: "white" }}>Email address</Form.Label>
          <Form.Control
            type="email"
            pattern="/^[a-zA-Z0-9.!#$%&â€™*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/"
            placeholder="Enter email"
            value={emailId}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <Form.Text className="text" style={{ color: "coral" }}>
            We'll never share your email with anyone else.
          </Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label style={{ color: "white" }}>Password</Form.Label>
          <Form.Control
            required
            type="password"
            pattern="(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}"
            placeholder="Password"
            value={password}
            onChange={(e) => setPass(e.target.value)}
          />
          <Form.Text className="text" style={{ color: "coral" }}>
            Password should contains atleast 8 characters
          </Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label style={{ color: "white" }}>Age</Form.Label>
          <Form.Control
            required
            type="number"
            placeholder="Age"
            value={age}
            onChange={(e) => setAge(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label style={{ color: "white" }}>Gender</Form.Label>
          <Form.Control
            required
            type="text"
            placeholder="Gender"
            value={gender}
            onChange={(e) => setGender(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label style={{ color: "white" }}>Mobile No.</Form.Label>
          <Form.Control
            required
            type="number"
            placeholder="Mobile No."
            value={ph_no}
            onChange={(e) => setPhone(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label style={{ color: "white" }}>Roles(user/admin)</Form.Label>
          <Form.Control
            required
            type="text"
            placeholder="user/admin"
            value={roles}
            onChange={(e) => setRoles(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="formBasicCheckbox">
          <Form.Check type="checkbox" label="Check me out" />
        </Form.Group>
        {/* <Button
          onClick={(o) => saveUser(o)}
          variant=" #2c7f86;"
          type="submit"
          id="submit"
        >
          Submit
        </Button> */}
        <SubmitBtn />
      </Form>
      <div>
        <footer id="footer2">
          <ul class="navbar-nav">
            <li class="nav-item">
              <img
                src={images1}
                alt="My Image"
                style={{ height: "56px", width: "5rem" }}
              />
            </li>
          </ul>
        </footer>
      </div>
    </div>
  );
};

export default Registration;
